mapper.insert:

insert into board (title, contents, hit, reg_date, g_no, o_no, depth, user_no)
	select #{title }, #{contents }, 0, now(), ifnull(max(g_no), 0) + 1, 1, 0, #{user_no }
	from board

mapper.select:

select g_no, o_no, depth 
	from board
	where no = #{no }

mapper.update:

update board
	set title = #{title }, contents = #{contents }
	where no = #{no }


update board
	set hit = hit + 1
	where no = #{no }
